import { Component, OnInit, OnDestroy } from '@angular/core';
import { SensorService } from '../../core/services/sensor.service';
import { AiService } from '../../core/services/ai.service';
import { Sensor } from '../../core/models/sensor.model';
import { interval, Subscription } from 'rxjs';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit, OnDestroy {
  sensors: Sensor[] = [];
  systemHealth = {
    status: 'excellent',
    message: 'All systems functioning optimally. Consider adjusting COâ‚‚ levels slightly for optimal tomato growth.'
  };
  chartData: any;
  private updateSubscription?: Subscription;
  roadmapStages = [
    { name: 'Germination', completed: true, active: false },
    { name: 'Seedling', completed: true, active: false },
    { name: 'Vegetation', completed: false, active: true },
    { name: 'Flowering', completed: false, active: false },
    { name: 'Fruiting', completed: false, active: false },
    { name: 'Harvest', completed: false, active: false }
  ];
  chartOptions: any;

  constructor(
    private sensorService: SensorService,
    private aiService: AiService
  ) { }

  ngOnInit(): void {
    this.loadSensorData();
    this.loadChartData();

    // Set up 5-second interval updates
    this.updateSubscription = interval(5000).subscribe(() => {
      this.loadChartData();
    });
  }

  ngOnDestroy(): void {
    if (this.updateSubscription) {
      this.updateSubscription.unsubscribe();
    }
  }

  loadSensorData(): void {
    this.sensorService.getECSensors().subscribe(
      (data) => {
        this.sensors = data;
      },
      (error) => {
        console.error('Error fetching sensor data:', error);
        // Fallback to mock data
        this.loadMockSensorData();
      }
    );
  }

  loadMockSensorData(): void {
    this.sensors = [
      { id: 1, name: 'Temperature', value: '24Â°C', icon: 'ðŸŒ¡ï¸', type: 'Environmental', unit: 'Â°C', status: 'normal' },
      { id: 2, name: 'Humidity', value: '65%', icon: 'ðŸ’§', type: 'Environmental', unit: '%', status: 'normal' },
      { id: 3, name: 'Light', value: '850 lux', icon: 'â˜€ï¸', type: 'Environmental', unit: 'lux', status: 'normal' },
      { id: 4, name: 'EC', value: '1.8 mS/cm', icon: 'âš¡', type: 'Nutrient', unit: 'mS/cm', status: 'normal' },
      { id: 5, name: 'pH', value: '6.2', icon: 'ðŸ§ª', type: 'Nutrient', unit: '', status: 'normal' },
      { id: 6, name: 'COâ‚‚', value: '450 ppm', icon: 'ðŸŒ«ï¸', type: 'Gas', unit: 'ppm', status: 'normal' }
    ];
  }

  loadChartData(): void {
    this.sensorService.getSensorHistory('defaultSensorId').subscribe(
      (data) => {
        this.processChartData(data);
      },
      (error) => {
        console.error('Error fetching chart data:', error);
        // Fallback to mock chart data
        this.loadMockChartData();
      }
    );
  }

  processChartData(data: any): void {
    // Process and format real data for chart
    this.chartData = {
      labels: data.timestamps,
      datasets: data.datasets
    };
  }

  loadMockChartData(): void {
    const now = new Date();
    const labels = Array.from({ length: 12 }, (_, i) => {
      const d = new Date(now.getTime() - (11 - i) * 5000);
      return d.toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit', second: '2-digit' });
    });

    this.chartData = {
      labels,
      datasets: [
        {
          label: 'Temperature (Â°C)',
          data: Array.from({ length: 12 }, () => 22 + Math.random() * 4),
          borderColor: '#006d5b',
          backgroundColor: 'rgba(0, 109, 91, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'Humidity (%)',
          data: Array.from({ length: 12 }, () => 60 + Math.random() * 12),
          borderColor: '#8d90bb',
          backgroundColor: 'rgba(141, 144, 187, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'Light (lux/100)',
          data: Array.from({ length: 12 }, () => Math.max(0, 5 + Math.random() * 3)),
          borderColor: '#ffc107',
          backgroundColor: 'rgba(255, 193, 7, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'EC (mS/cm)',
          data: Array.from({ length: 12 }, () => 1.5 + Math.random() * 0.6),
          borderColor: '#dc3545',
          backgroundColor: 'rgba(220, 53, 69, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
