import { Component, OnInit, Input } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent implements OnInit {
  @Input() collapsed: boolean = false;
  currentRoute: string = '';
  isMobileSidebarOpen: boolean = false;

  navItems = [
    { path: '/dashboard', icon: 'ðŸ“Š', label: 'Dashboard' },
    { path: '/my-farm', icon: 'ðŸŒ±', label: 'My Farm' },
    { path: '/crop-roadmap', icon: 'ðŸŒ¿', label: 'Crop Roadmap' },
    { path: '/ai-tips', icon: 'ðŸ¤–', label: 'AI Tips' },
    { path: '/logistics', icon: 'ðŸ“¦', label: 'Logistics' },
    { path: '/training', icon: 'ðŸŽ“', label: 'Training' },
    { path: '/partnerships', icon: 'ðŸ¤', label: 'Partnerships' },
    { path: '/settings', icon: 'âš™ï¸', label: 'Settings' }
  ];

  constructor(private router: Router) { }

  ngOnInit(): void {
    this.router.events.pipe(
      filter(event => event instanceof NavigationEnd)
    ).subscribe((event: any) => {
      this.currentRoute = event.url;
    });
  }

  navigateTo(path: string): void {
    this.router.navigate([path]);
    if (window.innerWidth <= 768) {
      this.toggleMobileSidebar();
    }
  }

  toggleMobileSidebar(): void {
    this.isMobileSidebarOpen = !this.isMobileSidebarOpen;
  }
}
