import { Component, OnInit, OnDestroy } from '@angular/core';
import { SensorService } from '../../core/services/sensor.service';
import { AiService } from '../../core/services/ai.service';
import { interval, Subject, combineLatest } from 'rxjs';
import { takeUntil, map } from 'rxjs/operators';
import { Sensor } from '../../core/models/sensor.model';

interface CurrentMetrics {
  temperature: number;
  humidity: number;
  light: number;
  ec: number;
  ph: number;
}

interface ChartData {
  timestamps: string[];
  datasets: Array<{
    label: string;
    data: number[];
    borderColor: string;
    backgroundColor: string;
    tension: number;
    fill: boolean;
    borderWidth: number;
  }>;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();
  private updateInterval$ = interval(5000);

  currentMetrics: CurrentMetrics = {
    temperature: 24,
    humidity: 65,
    light: 650,
    ec: 1.8,
    ph: 6.2
  };

  systemHealth = {
    status: 'excellent',
    message: 'All systems functioning optimally. Consider adjusting CO₂ levels slightly for optimal tomato growth.'
  };

  chartData: ChartData | null = null;
  chartOptions: any;

  roadmapStages = [
    { name: 'Germination', completed: true, active: false },
    { name: 'Seedling', completed: true, active: false },
    { name: 'Vegetation', completed: false, active: true },
    { name: 'Flowering', completed: false, active: false },
    { name: 'Fruiting', completed: false, active: false },
    { name: 'Harvest', completed: false, active: false }
  ];

  constructor(
    private sensorService: SensorService,
    private aiService: AiService
  ) {}

  ngOnInit(): void {
    this.loadInitialData();
    this.setupRealTimeUpdates();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  private loadInitialData(): void {
    this.loadCurrentMetrics();
    this.loadChartData();
  }

  private setupRealTimeUpdates(): void {
    this.updateInterval$.pipe(
      takeUntil(this.destroy$)
    ).subscribe(() => {
      this.loadCurrentMetrics();
      this.loadChartData();
    });
  }

  private loadCurrentMetrics(): void {
    combineLatest([
      this.sensorService.getTemperatureSensors(),
      this.sensorService.getHumiditySensors(),
      this.sensorService.getLightSensors(),
      this.sensorService.getECSensors(),
      this.sensorService.getPHSensors()
    ]).pipe(
      takeUntil(this.destroy$),
      map(([temp, humidity, light, ec, ph]) => ({
        temperature: Number(temp[0]?.value ?? 24),
        humidity: Number(humidity[0]?.value ?? 65),
        light: Number(light[0]?.value ?? 650),
        ec: Number(ec[0]?.value ?? 1.8),
        ph: Number(ph[0]?.value ?? 6.2)
      } as CurrentMetrics))
    ).subscribe({
      next: (metrics: CurrentMetrics) => {
        this.currentMetrics = metrics;
      },
      error: (error: Error) => {
        console.error('Error loading current metrics:', error);
        // Keep using last known values
      }
    });
  }

  private loadChartData(): void {
    // Get history for all sensor types
    const sensorIds = ['temp-1', 'humidity-1', 'light-1', 'ec-1', 'ph-1'];
    const histories$ = sensorIds.map(id => this.sensorService.getSensorHistory(id, 1));

    combineLatest(histories$).pipe(
      takeUntil(this.destroy$),
      map(histories => this.processHistoriesIntoChartData(histories))
    ).subscribe({
      next: (chartData: ChartData) => {
        this.chartData = chartData;
      },
      error: (error: Error) => {
        console.error('Error loading chart data:', error);
        this.loadMockChartData();
      }
    });
  }

  private processHistoriesIntoChartData(histories: any[][]): ChartData {
    // Process the raw histories into chart data format
    const timestamps = histories[0]?.map(h => new Date(h.timestamp).toLocaleTimeString()) ?? [];
    const datasets = [
      {
        label: 'Temperature (°C)',
        data: histories[0]?.map(h => Number(h.value)) ?? [],
        borderColor: '#006d5b',
        backgroundColor: 'rgba(0, 109, 91, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Humidity (%)',
        data: histories[1]?.map(h => Number(h.value)) ?? [],
        borderColor: '#8d90bb',
        backgroundColor: 'rgba(141, 144, 187, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Light (lux/100)',
        data: histories[2]?.map(h => Number(h.value)) ?? [],
        borderColor: '#ffc107',
        backgroundColor: 'rgba(255, 193, 7, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'EC (mS/cm)',
        data: histories[3]?.map(h => Number(h.value)) ?? [],
        borderColor: '#dc3545',
        backgroundColor: 'rgba(220, 53, 69, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'pH',
        data: histories[4]?.map(h => Number(h.value)) ?? [],
        borderColor: '#6610f2',
        backgroundColor: 'rgba(102, 16, 242, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      }
    ];

    return { timestamps, datasets };
  }

  private loadMockChartData(): void {
    const now = new Date();
    const labels = Array.from({ length: 12 }, (_, i) => {
      const d = new Date(now.getTime() - (11 - i) * 5000);
      return d.toLocaleTimeString('en-US', { hour12: false });
    });

    this.chartData = {
      timestamps: labels,
      datasets: [
        {
          label: 'Temperature (°C)',
          data: Array.from({ length: 12 }, () => 22 + Math.random() * 4),
          borderColor: '#006d5b',
          backgroundColor: 'rgba(0, 109, 91, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'Humidity (%)',
          data: Array.from({ length: 12 }, () => 60 + Math.random() * 12),
          borderColor: '#8d90bb',
          backgroundColor: 'rgba(141, 144, 187, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'Light (lux/100)',
          data: Array.from({ length: 12 }, () => Math.max(0, 5 + Math.random() * 3)),
          borderColor: '#ffc107',
          backgroundColor: 'rgba(255, 193, 7, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'EC (mS/cm)',
          data: Array.from({ length: 12 }, () => 1.5 + Math.random() * 0.6),
          borderColor: '#dc3545',
          backgroundColor: 'rgba(220, 53, 69, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'pH',
          data: Array.from({ length: 12 }, () => 5.8 + Math.random() * 0.8),
          borderColor: '#6610f2',
          backgroundColor: 'rgba(102, 16, 242, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        }
      ]
    };
  }

  startIrrigation(): void {
    this.sensorService.startIrrigation().subscribe(
      () => {
        console.log('Irrigation started');
      },
      error => {
        console.error('Error starting irrigation:', error);
      }
    );
  }

  exportData(): void {
    this.sensorService.exportData().subscribe(
      (data) => {
        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'sensor-data.json';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
      },
      error => {
        console.error('Error exporting data:', error);
      }
    );
  }
}
