import { Component, OnInit, OnDestroy, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { SensorService } from '../../core/services/sensor.service';
import { AiService } from '../../core/services/ai.service';
import { GreenhouseService, GreenhouseData } from '../../core/services/greenhouse.service';
import { Sensor } from '../../core/models/sensor.model';
import { Subscription } from 'rxjs';
import { Chart, ChartConfiguration } from 'chart.js/auto';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit, OnDestroy, AfterViewInit {
  @ViewChild('chartCanvas') chartCanvas!: ElementRef;

  sensors: Sensor[] = [];
  systemHealth = {
    status: 'excellent',
    message: 'All systems functioning optimally. Consider adjusting COâ‚‚ levels slightly for optimal tomato growth.'
  };

  greenhouseChart: any;
  private greenhouseDataSubscription: Subscription;
  currentData: GreenhouseData;

  // Sensor data
  currentTemperature: number = 25;
  currentHumidity: number = 60;
  currentLight: number = 1200;

  private chart: Chart | undefined;

  constructor(
    private sensorService: SensorService,
    private aiService: AiService,
    private greenhouseService: GreenhouseService
  ) { }

  ngOnInit(): void {
    this.loadSensorData();
    this.initializeChart();
    this.subscribeToGreenhouseData();

    // Simulate real-time data updates
    setInterval(() => {
      this.updateSensorData();
    }, 5000);
  }

  ngOnDestroy(): void {
    if (this.greenhouseDataSubscription) {
      this.greenhouseDataSubscription.unsubscribe();
    }
  }

  ngAfterViewInit(): void {
    this.initializeChart();
  }

  private initializeChart(): void {
    const ctx = this.chartCanvas.nativeElement.getContext('2d');

    const config: ChartConfiguration = {
      type: 'line',
      data: {
        labels: Array.from({ length: 10 }, (_, i) => `${i * 5}m ago`),
        datasets: [
          {
            label: 'Temperature (Â°C)',
            data: Array.from({ length: 10 }, () => Math.random() * 10 + 20),
            borderColor: '#ff6384',
            tension: 0.4
          },
          {
            label: 'Humidity (%)',
            data: Array.from({ length: 10 }, () => Math.random() * 20 + 50),
            borderColor: '#36a2eb',
            tension: 0.4
          }
        ]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    };

    this.chart = new Chart(ctx, config);
  }

  private subscribeToGreenhouseData(): void {
    this.greenhouseDataSubscription = this.greenhouseService.getGreenhouseData()
      .subscribe(data => {
        if (data.length > 0) {
          this.updateChart(data);
          this.currentData = data[data.length - 1];
          this.updateSensors();
        }
      });
  }

  private updateChart(data: GreenhouseData[]): void {
    const labels = data.map(d => {
      const time = new Date(d.timestamp);
      return time.toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' });
    });

    const temperatures = data.map(d => d.temperature);
    const humidities = data.map(d => d.humidity);
    const lights = data.map(d => d.light);

    this.greenhouseChart.data.labels = labels;
    this.greenhouseChart.data.datasets[0].data = temperatures;
    this.greenhouseChart.data.datasets[1].data = humidities;
    this.greenhouseChart.data.datasets[2].data = lights;
    this.greenhouseChart.update();
  }

  private updateSensors(): void {
    if (this.currentData) {
      this.sensors = [
        {
          id: 1,
          name: 'Temperature',
          value: `${this.currentData.temperature}Â°C`,
          icon: 'ðŸŒ¡ï¸',
          type: 'Environmental',
          unit: 'Â°C',
          status: 'normal'
        },
        {
          id: 2,
          name: 'Humidity',
          value: `${this.currentData.humidity}%`,
          icon: 'ðŸ’§',
          type: 'Environmental',
          unit: '%',
          status: 'normal'
        },
        {
          id: 3,
          name: 'Light',
          value: `${this.currentData.light} lux`,
          icon: 'â˜€ï¸',
          type: 'Environmental',
          unit: 'lux',
          status: 'normal'
        }
      ];
    }
  }

  loadSensorData(): void {
    // Keep other sensor data for EC, pH, and COâ‚‚
    const otherSensors = [
      { id: 4, name: 'EC', value: '1.8 mS/cm', icon: 'âš¡', type: 'Nutrient', unit: 'mS/cm', status: 'normal' },
      { id: 5, name: 'pH', value: '6.2', icon: 'ðŸ§ª', type: 'Nutrient', unit: '', status: 'normal' },
      { id: 6, name: 'COâ‚‚', value: '450 ppm', icon: 'ðŸŒ«ï¸', type: 'Gas', unit: 'ppm', status: 'normal' }
    ];
    this.sensors = [...this.sensors, ...otherSensors];
  }

  startIrrigation(): void {
    this.sensorService.startIrrigation().subscribe(
      () => alert('Irrigation started successfully!'),
      (error) => console.error('Error starting irrigation:', error)
    );
  }

  exportData(): void {
    this.sensorService.exportData().subscribe(
      (data) => {
        const blob = new Blob([data], { type: 'application/json' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.setAttribute('hidden', '');
        a.setAttribute('href', url);
        a.setAttribute('download', 'eden-link-data-export.json');
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
      },
      (error) => console.error('Error exporting data:', error)
    );
  }

  private updateSensorData(): void {
    // Simulate sensor data changes
    this.currentTemperature = Math.round((Math.random() * 5 + 22) * 10) / 10;
    this.currentHumidity = Math.round(Math.random() * 20 + 50);
    this.currentLight = Math.round(Math.random() * 500 + 1000);

    // Update chart data
    if (this.chart) {
      this.chart.data.datasets[0].data.shift();
      this.chart.data.datasets[0].data.push(this.currentTemperature);
      this.chart.data.datasets[1].data.shift();
      this.chart.data.datasets[1].data.push(this.currentHumidity);
      this.chart.update();
    }
  }

  // Status getters
  getTemperatureStatus(): string {
    if (this.currentTemperature < 18) return 'alert';
    if (this.currentTemperature > 30) return 'warning';
    return 'normal';
  }

  getHumidityStatus(): string {
    if (this.currentHumidity < 40) return 'alert';
    if (this.currentHumidity > 80) return 'warning';
    return 'normal';
  }

  getLightStatus(): string {
    if (this.currentLight < 800) return 'alert';
    if (this.currentLight > 2000) return 'warning';
    return 'normal';
  }

  getSystemHealthClass(): string {
    const statuses = [
      this.getTemperatureStatus(),
      this.getHumidityStatus(),
      this.getLightStatus()
    ];
    if (statuses.includes('alert')) return 'health-alert';
    if (statuses.includes('warning')) return 'health-warning';
    return 'health-normal';
  }

  getSystemHealthIcon(): string {
    const healthClass = this.getSystemHealthClass();
    switch (healthClass) {
      case 'health-alert': return 'fa-exclamation-circle';
      case 'health-warning': return 'fa-exclamation-triangle';
      default: return 'fa-check-circle';
    }
  }

  getSystemHealthMessage(): string {
    const healthClass = this.getSystemHealthClass();
    switch (healthClass) {
      case 'health-alert': return 'System requires immediate attention';
      case 'health-warning': return 'System needs monitoring';
      default: return 'All systems operating normally';
    }
  }

