import { Component, OnInit, OnDestroy } from '@angular/core';
import { SensorService } from '../../core/services/sensor.service';
import { AiService } from '../../core/services/ai.service';
import { Sensor } from '../../core/models/sensor.model';
import { interval, Subscription, Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

interface CurrentMetrics {
  temperature: number;
  humidity: number;
  light: number;
  ec: number;
  ph: number;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit, OnDestroy {
  sensors: Sensor[] = [];
  systemHealth = {
    status: 'excellent',
    message: 'All systems functioning optimally. Consider adjusting CO₂ levels slightly for optimal tomato growth.'
  };
  chartData: any;
  private updateSubscription?: Subscription;
  roadmapStages = [
    { name: 'Germination', completed: true, active: false },
    { name: 'Seedling', completed: true, active: false },
    { name: 'Vegetation', completed: false, active: true },
    { name: 'Flowering', completed: false, active: false },
    { name: 'Fruiting', completed: false, active: false },
    { name: 'Harvest', completed: false, active: false }
  ];
  chartOptions: any;
  private destroy$ = new Subject<void>();
  private updateInterval$ = interval(5000);

  currentMetrics: CurrentMetrics = {
    temperature: 24,
    humidity: 65,
    light: 650,
    ec: 1.8,
  constructor(
    private sensorService: SensorService,
    private aiService: AiService
  ) { }

  ngOnInit(): void {
    this.loadSensorData();
    this.loadChartData();

    // Set up 5-second interval updates
    this.updateSubscription = interval(5000).subscribe(() => {
      this.loadChartData();
    });
  }

  ngOnDestroy(): void {
    if (this.updateSubscription) {
      this.updateSubscription.unsubscribe();
    }
  }

  loadSensorData(): void {
    this.sensorService.getECSensors().subscribe(
      (data) => {
        this.sensors = data;
      },
      (error) => {
        console.error('Error fetching sensor data:', error);
        // Fallback to mock data
        this.loadMockSensorData();
      }
    );
  }

  loadMockSensorData(): void {
    this.sensors = [
      { id: 1, name: 'Temperature', value: '24°C', icon: '🌡️', type: 'Environmental', unit: '°C', status: 'normal' },
      { id: 2, name: 'Humidity', value: '65%', icon: '💧', type: 'Environmental', unit: '%', status: 'normal' },
      { id: 3, name: 'Light', value: '850 lux', icon: '☀️', type: 'Environmental', unit: 'lux', status: 'normal' },
      { id: 4, name: 'EC', value: '1.8 mS/cm', icon: '⚡', type: 'Nutrient', unit: 'mS/cm', status: 'normal' },
      { id: 5, name: 'pH', value: '6.2', icon: '🧪', type: 'Nutrient', unit: '', status: 'normal' },
      { id: 6, name: 'CO₂', value: '450 ppm', icon: '🌫️', type: 'Gas', unit: 'ppm', status: 'normal' }
    ];
  }

  loadChartData(): void {
    this.sensorService.getSensorHistory('defaultSensorId').subscribe(
      (data) => {
        this.processChartData(data);
      },
      (error) => {
        console.error('Error fetching chart data:', error);
        // Fallback to mock chart data
        this.loadMockChartData();
      }
    );
  }

  processChartData(data: any): void {
    // Process and format real data for chart
    this.chartData = {
      labels: data.timestamps,
      datasets: data.datasets
    };
  }

