// src/app/features/logistics/logistics.component.ts
import { Component, OnInit, OnDestroy } from '@angular/core';
import { LogisticsService } from '../../core/services/logistics.service';
import { SystemStatus, NutrientLevel, WaterLevel, UsageHistory, PumpStatus } from '../../core/models/system-status.model';
import { ChartData, ChartOptions } from 'chart.js';
import { Subject, Subscription } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { interval } from 'rxjs';

interface SensorHistory {
  timestamps?: string[];
  temperature?: number[];
  humidity?: number[];
  light?: number[];
  ph?: number[];
}

@Component({
  selector: 'app-logistics',
  templateUrl: './logistics.component.html',
  styleUrls: ['./logistics.component.css']
})
export class LogisticsComponent implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();
  private updateSubscription?: Subscription;

  systemStatus: SystemStatus = {
    connectionStatus: 'disconnected',
    powerStatus: 'off',
    automationMode: 'manual',
    pumps: [] as PumpStatus[],
    lastUpdated: new Date(),
    status: 'operational',
    message: 'System initialized'
  };

  nutrientLevels: NutrientLevel[] = [{
    id: 'nutrient-a',
    name: 'Nutrient A',
    level: 0,
    percentage: 0,
    unit: 'ppm',
    status: 'normal',
    lastUpdated: new Date()
  }];

  waterLevel: WaterLevel = {
    current: 0,
    capacity: 100,
    percentage: 0,
    status: 'normal',
    lastUpdated: new Date()
  };

  isLoading = true;
  isAutoMode = true;

  // Updated chart properties
  waterChartData: any = {
    labels: [],
    datasets: [{
      label: 'Water Usage (L)',
      data: [],
      borderColor: '#006d5b',
      backgroundColor: 'rgba(0, 109, 91, 0.1)',
      tension: 0.4,
      fill: true,
      borderWidth: 2
    }]
  };

  nutrientChartData: any = {
    labels: [],
    datasets: [{
      label: 'Nutrient Usage (mL)',
      data: [],
      borderColor: '#dc3545',
      backgroundColor: 'rgba(220, 53, 69, 0.1)',
      tension: 0.4,
      fill: true,
      borderWidth: 2
    }]
  };

  chartOptions: any = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        grid: {
          color: 'rgba(0, 0, 0, 0.1)',
          borderColor: 'rgba(0, 0, 0, 0.3)'
        },
        ticks: {
          color: '#666',
          font: {
            size: 11
          }
        }
      },
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(0, 0, 0, 0.1)',
          borderColor: 'rgba(0, 0, 0, 0.3)'
        },
        ticks: {
          color: '#666',
          font: {
            size: 11
          }
        }
      }
    },
    plugins: {
      legend: {
        position: 'top',
        align: 'center',
        labels: {
          boxWidth: 15,
          padding: 15,
          font: {
            size: 11
          }
        }
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        titleColor: '#000',
        bodyColor: '#666',
        borderColor: 'rgba(0, 0, 0, 0.1)',
        borderWidth: 1,
        padding: 10
      }
    },
    interaction: {
      mode: 'nearest',
      axis: 'x',
      intersect: false
    }
  };

  // Current metric values
  currentTemp: number = 24;
  currentHumidity: number = 65;
  currentLight: number = 650;
  currentPH: number = 6.2;

  // Chart data with optimal ranges
  chartData: any = {
    labels: [],
    datasets: [
      {
        label: 'Temperature (°C)',
        data: [],
        borderColor: '#006d5b',
        backgroundColor: 'rgba(0, 109, 91, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Optimal Temperature',
        data: [],
        borderColor: '#2ecc71',
        borderDash: [5, 5],
        fill: false,
        tension: 0
      },
      {
        label: 'Humidity (%)',
        data: [],
        borderColor: '#8d90bb',
        backgroundColor: 'rgba(141, 144, 187, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Optimal Humidity',
        data: [],
        borderColor: '#2ecc71',
        borderDash: [5, 5],
        fill: false,
        tension: 0
      },
      {
        label: 'Light (lux)',
        data: [],
        borderColor: '#ffc107',
        backgroundColor: 'rgba(255, 193, 7, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Optimal Light',
        data: [],
        borderColor: '#2ecc71',
        borderDash: [5, 5],
        fill: false,
        tension: 0
      },
      {
        label: 'pH Level',
        data: [],
        borderColor: '#6610f2',
        backgroundColor: 'rgba(102, 16, 242, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Optimal pH',
        data: [],
        borderColor: '#2ecc71',
        borderDash: [5, 5],
        fill: false,
        tension: 0
      }
    ]
  };

  constructor(private logisticsService: LogisticsService) {}

  ngOnInit(): void {
    this.loadLogisticsData();
    this.loadUsageChart();
    this.loadChartData();

    // Set up real-time updates
    this.updateSubscription = interval(5000).subscribe(() => {
      this.updateCurrentMetrics();
      this.loadChartData();
    });
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  loadLogisticsData(): void {
    this.isLoading = true;

    this.logisticsService.getNutrientLevels().pipe(takeUntil(this.destroy$)).subscribe({
      next: (nutrients) => {
        this.nutrientLevels = nutrients;
      },
      error: (error) => {
        console.error('Error loading nutrient levels:', error);
      }
    });

    this.logisticsService.getWaterLevel().pipe(takeUntil(this.destroy$)).subscribe({
      next: (water) => {
        this.waterLevel = water;
      },
      error: (error) => {
        console.error('Error loading water level:', error);
      }
    });

    this.logisticsService.getSystemStatus().pipe(takeUntil(this.destroy$)).subscribe({
      next: (status) => {
        this.systemStatus = status;
        this.isAutoMode = status.automationMode === 'auto';
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Error loading system status:', error);
        this.isLoading = false;
      }
    });
  }

  loadUsageChart(): void {
    this.logisticsService.getUsageHistory().pipe(takeUntil(this.destroy$)).subscribe({
      next: (data) => {
        // Update water usage chart
        this.waterChartData = {
          labels: data.dates,
          datasets: [{
            label: 'Water Usage (L)',
            data: data.waterUsage,
            borderColor: '#006d5b',
            backgroundColor: 'rgba(0, 109, 91, 0.1)',
            tension: 0.4,
            fill: true,
            borderWidth: 2
          }]
        };

        // Update nutrient usage chart
        this.nutrientChartData = {
          labels: data.dates,
          datasets: [{
            label: 'Nutrient Usage (mL)',
            data: data.nutrientAUsage,
            borderColor: '#dc3545',
            backgroundColor: 'rgba(220, 53, 69, 0.1)',
            tension: 0.4,
            fill: true,
            borderWidth: 2
          }]
        };
      },
      error: (error) => {
        console.error('Error loading usage history:', error);
      }
    });
  }

  toggleAutomationMode(): void {
    const newMode = this.isAutoMode ? 'manual' : 'auto';

    this.logisticsService.setAutomationMode(newMode).pipe(takeUntil(this.destroy$)).subscribe({
      next: (status) => {
        this.systemStatus = status;
        this.isAutoMode = (status && status.automationMode?.toLowerCase?.()) === 'auto';
        console.log(`Automation mode set to ${this.isAutoMode ? 'auto' : 'manual'}`);
      },
      error: (error) => {
        console.error('Error toggling automation mode:', error);
        // Reset the toggle if there was an error
        this.isAutoMode = !this.isAutoMode;
      }
    });
  }

  activatePump(pumpId: string): void {
    this.logisticsService.activatePump(pumpId).pipe(takeUntil(this.destroy$)).subscribe({
      next: (status) => {
        this.systemStatus = status;
      },
      error: (error) => {
        console.error(`Error activating pump ${pumpId}:`, error);
      }
    });
  }

  refillNutrient(nutrientId: string): void {
    this.logisticsService.confirmNutrientRefill(nutrientId).pipe(takeUntil(this.destroy$)).subscribe({
      next: (nutrients) => {
        this.nutrientLevels = nutrients;
      },
      error: (error) => {
        console.error(`Error confirming refill for nutrient ${nutrientId}:`, error);
      }
    });
  }

  refillWater(): void {
    this.logisticsService.confirmWaterRefill().pipe(takeUntil(this.destroy$)).subscribe({
      next: (water) => {
        this.waterLevel = water;
      },
      error: (error) => {
        console.error('Error confirming water refill:', error);
      }
    });
  }

  getStatusClass(percentage: number): string {
    if (percentage <= 20) return 'critical';
    if (percentage <= 40) return 'warning';
    return 'normal';
  }

  updateCurrentMetrics(): void {
    // Simulate real-time updates with small random variations
    this.currentTemp = Math.round((24 + (Math.random() * 4 - 2)) * 10) / 10;
    this.currentHumidity = Math.round((65 + (Math.random() * 10 - 5)) * 10) / 10;
    this.currentLight = Math.round(650 + (Math.random() * 300 - 150));
    this.currentPH = Math.round((6.2 + (Math.random() * 0.6 - 0.3)) * 100) / 100;
  }

  getMetricStatus(value: number, min: number, max: number): string {
    if (value >= min && value <= max) {
      return 'status-optimal';
    } else if (value < min - ((min * 10) / 100) || value > max + ((max * 10) / 100)) {
      return 'status-critical';
    } else {
      return 'status-warning';
    }
  }

  loadChartData(): void {
    this.logisticsService.getSensorHistory('defaultSensorId').subscribe({
      next: (data) => {
        const timestamps = data.timestamps || Array.from({ length: 12 }, (_, i) => {
          const d = new Date();
          d.setMinutes(d.getMinutes() - (11 - i) * 5);
          return d.toLocaleTimeString('en-US', { hour12: false });
        });

        // Update chart data with both actual values and optimal ranges
        this.chartData = {
          labels: timestamps,
          datasets: [
            {
              label: 'Temperature (°C)',
              data: data.temperature || Array.from({ length: 12 }, () => 22 + Math.random() * 4),
              borderColor: '#006d5b',
              backgroundColor: 'rgba(0, 109, 91, 0.1)',
              tension: 0.4,
              fill: true,
              borderWidth: 2
            },
            {
              label: 'Optimal Temperature',
              data: Array(12).fill(24), // Optimal temperature line
              borderColor: '#2ecc71',
              borderDash: [5, 5],
              fill: false,
              tension: 0
            },
            {
              label: 'Humidity (%)',
              data: data.humidity || Array.from({ length: 12 }, () => 60 + Math.random() * 12),
              borderColor: '#8d90bb',
              backgroundColor: 'rgba(141, 144, 187, 0.1)',
              tension: 0.4,
              fill: true,
              borderWidth: 2
            },
            {
              label: 'Optimal Humidity',
              data: Array(12).fill(65), // Optimal humidity line
              borderColor: '#2ecc71',
              borderDash: [5, 5],
              fill: false,
              tension: 0
            },
            {
              label: 'Light (lux)',
              data: data.light || Array.from({ length: 12 }, () => 500 + Math.random() * 300),
              borderColor: '#ffc107',
              backgroundColor: 'rgba(255, 193, 7, 0.1)',
              tension: 0.4,
              fill: true,
              borderWidth: 2
            },
            {
              label: 'Optimal Light',
              data: Array(12).fill(650), // Optimal light line
              borderColor: '#2ecc71',
              borderDash: [5, 5],
              fill: false,
              tension: 0
            },
            {
              label: 'pH Level',
              data: data.ph || Array.from({ length: 12 }, () => 5.8 + Math.random() * 0.8),
              borderColor: '#6610f2',
              backgroundColor: 'rgba(102, 16, 242, 0.1)',
              tension: 0.4,
              fill: true,
              borderWidth: 2
            },
            {
              label: 'Optimal pH',
              data: Array(12).fill(6.2), // Optimal pH line
              borderColor: '#2ecc71',
              borderDash: [5, 5],
              fill: false,
              tension: 0
            }
          ]
        };
      },
      error: (error) => {
        console.error('Error loading sensor history:', error);
      }
    });
  }
}
