import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

interface SystemSettings {
  notifications: {
    emailAlerts: boolean;
    pushNotifications: boolean;
    smsAlerts: boolean;
  };
  dataManagement: {
    dataRetentionPeriod: number;
    autoBackup: boolean;
    backupFrequency: string;
  };
}

interface MonitoringSettings {
  sensorPollingInterval: number;
  thresholds: {
    temperature: { min: number; max: number; };
    humidity: { min: number; max: number; };
    light: { min: number; max: number; };
    ec: { min: number; max: number; };
    ph: { min: number; max: number; };
  };
}

interface SystemVersion {
  current: string;
  latest: string;
  updateAvailable: boolean;
}

@Component({
  selector: 'app-settings',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './settings.component.html',
  styleUrl: './settings.component.css'
})
export class SettingsComponent implements OnInit {
  systemSettings: SystemSettings = {
    notifications: {
      emailAlerts: true,
      pushNotifications: true,
      smsAlerts: false
    },
    dataManagement: {
      dataRetentionPeriod: 90,
      autoBackup: true,
      backupFrequency: 'weekly'
    }
  };

  monitoringSettings: MonitoringSettings = {
    sensorPollingInterval: 30,
    thresholds: {
      temperature: { min: 20, max: 30 },
      humidity: { min: 40, max: 70 },
      light: { min: 2000, max: 6000 },
      ec: { min: 1.0, max: 2.5 },
      ph: { min: 5.5, max: 6.5 }
    }
  };

  systemVersion: SystemVersion = {
    current: '1.0.0',
    latest: '1.0.1',
    updateAvailable: true
  };

  backupFrequencyOptions = [
    { value: 'daily', label: 'Daily' },
    { value: 'weekly', label: 'Weekly' },
    { value: 'monthly', label: 'Monthly' }
  ];

  constructor() { }

  ngOnInit(): void {
    // In a real application, these settings would be loaded from a service
    this.loadSettings();
  }

  saveSettings(): void {
    // In a real application, this would save to a backend service
    console.log('Saving settings:', {
      system: this.systemSettings,
      monitoring: this.monitoringSettings
    });
    // Mock successful save
    alert('Settings saved successfully!');
  }

  backupSystem(): void {
    // Mock backup process
    console.log('Initiating system backup...');
    setTimeout(() => {
      alert('System backup completed successfully!');
    }, 2000);
  }

  restoreFromBackup(): void {
    // Mock restore process
    if (confirm('Are you sure you want to restore from the last backup? This will override current settings.')) {
      console.log('Initiating system restore...');
      setTimeout(() => {
        alert('System restored successfully!');
        this.loadSettings(); // Reload settings after restore
      }, 2000);
    }
  }

  checkForUpdates(): void {
    // Mock update check
    console.log('Checking for updates...');
    setTimeout(() => {
      this.systemVersion = {
        ...this.systemVersion,
        updateAvailable: true,
        latest: '1.0.2'
      };
      alert('New update available: v1.0.2');
    }, 1500);
  }

  private loadSettings(): void {
    // In a real application, this would load from a service
    // For now, we'll use the default values set in the properties
    console.log('Loading settings...');
  }
}
<div class="settings-container">
  <div class="settings-header">
    <h1>System Settings</h1>
    <button class="btn-primary" (click)="saveSettings()">Save Changes</button>
  </div>

  <div class="settings-grid">
    <!-- Notifications Section -->
    <div class="settings-section">
      <h2>Notifications</h2>
      <div class="settings-card">
        <div class="setting-item">
          <label>
            <input type="checkbox" [(ngModel)]="systemSettings.notifications.emailAlerts">
            Email Alerts
          </label>
        </div>
        <div class="setting-item">
          <label>
            <input type="checkbox" [(ngModel)]="systemSettings.notifications.pushNotifications">
            Push Notifications
          </label>
        </div>
        <div class="setting-item">
          <label>
            <input type="checkbox" [(ngModel)]="systemSettings.notifications.smsAlerts">
            SMS Alerts
          </label>
        </div>
      </div>
    </div>

    <!-- Data Management Section -->
    <div class="settings-section">
      <h2>Data Management</h2>
      <div class="settings-card">
        <div class="setting-item">
          <label>Data Retention Period (days)</label>
          <input type="number" [(ngModel)]="systemSettings.dataManagement.dataRetentionPeriod" min="1" max="365">
        </div>
        <div class="setting-item">
          <label>
            <input type="checkbox" [(ngModel)]="systemSettings.dataManagement.autoBackup">
            Auto Backup
          </label>
        </div>
        <div class="setting-item">
          <label>Backup Frequency</label>
          <select [(ngModel)]="systemSettings.dataManagement.backupFrequency">
            <option *ngFor="let option of backupFrequencyOptions" [value]="option.value">
              {{option.label}}
            </option>
          </select>
        </div>
        <div class="setting-item buttons">
          <button class="btn-outline" (click)="backupSystem()">Backup Now</button>
          <button class="btn-outline" (click)="restoreFromBackup()">Restore from Backup</button>
        </div>
      </div>
    </div>

    <!-- Monitoring Settings Section -->
    <div class="settings-section">
      <h2>Monitoring Settings</h2>
      <div class="settings-card">
        <div class="setting-item">
          <label>Sensor Polling Interval (seconds)</label>
          <input type="number" [(ngModel)]="monitoringSettings.sensorPollingInterval" min="5" max="300">
        </div>

        <h3>Threshold Settings</h3>
        <div class="threshold-grid">
          <div class="threshold-item">
            <label>Temperature (°C)</label>
            <div class="range-inputs">
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.temperature.min" placeholder="Min">
              <span>to</span>
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.temperature.max" placeholder="Max">
            </div>
          </div>

          <div class="threshold-item">
            <label>Humidity (%)</label>
            <div class="range-inputs">
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.humidity.min" placeholder="Min">
              <span>to</span>
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.humidity.max" placeholder="Max">
            </div>
          </div>

          <div class="threshold-item">
            <label>Light (lux)</label>
            <div class="range-inputs">
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.light.min" placeholder="Min">
              <span>to</span>
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.light.max" placeholder="Max">
            </div>
          </div>

          <div class="threshold-item">
            <label>EC (mS/cm)</label>
            <div class="range-inputs">
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.ec.min" placeholder="Min" step="0.1">
              <span>to</span>
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.ec.max" placeholder="Max" step="0.1">
            </div>
          </div>

          <div class="threshold-item">
            <label>pH</label>
            <div class="range-inputs">
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.ph.min" placeholder="Min" step="0.1">
              <span>to</span>
              <input type="number" [(ngModel)]="monitoringSettings.thresholds.ph.max" placeholder="Max" step="0.1">
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- System Information Section -->
    <div class="settings-section">
      <h2>System Information</h2>
      <div class="settings-card">
        <div class="setting-item">
          <label>Current Version</label>
          <span>{{systemVersion.current}}</span>
        </div>
        <div class="setting-item">
          <label>Latest Version</label>
          <span>{{systemVersion.latest}}</span>
        </div>
        <div class="setting-item">
          <button 
            class="btn-primary" 
            (click)="checkForUpdates()"
            [disabled]="systemVersion.updateAvailable">
            {{ systemVersion.updateAvailable ? 'Update Available!' : 'Check for Updates' }}
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
