import { Component, OnInit, OnDestroy, ViewChild, ElementRef } from '@angular/core';
import { SensorService } from '../../core/services/sensor.service';
import { AiService } from '../../core/services/ai.service';
import { Sensor } from '../../core/models/sensor.model';
import { Chart, ChartConfiguration } from 'chart.js/auto';
import { SensorDataService, SensorData } from '../../services/sensor-data.service';
import { Subscription } from 'rxjs';

interface SystemHealth {
  status: 'excellent' | 'good' | 'warning' | 'error';
  message: string;
  cpuUsage: number;
  memoryUsage: number;
  storageUsage: number;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit, OnDestroy {
  @ViewChild('chartCanvas') chartCanvas!: ElementRef;

  private chart: Chart | undefined;
  private dataSubscription: Subscription | undefined;

  sensors: Sensor[] = [];
  systemHealth: SystemHealth = {
    status: 'excellent',
    message: 'All systems functioning optimally. Consider adjusting COâ‚‚ levels slightly for optimal tomato growth.',
    cpuUsage: 45,
    memoryUsage: 60,
    storageUsage: 55
  };
  chartData: any;
  roadmapStages = [
    { name: 'Germination', completed: true, active: false },
    { name: 'Seedling', completed: true, active: false },
    { name: 'Vegetation', completed: false, active: true },
    { name: 'Flowering', completed: false, active: false },
    { name: 'Fruiting', completed: false, active: false },
    { name: 'Harvest', completed: false, active: false }
  ];

  constructor(
    private sensorService: SensorService,
    private aiService: AiService,
    private sensorDataService: SensorDataService
  ) { }

  ngOnInit(): void {
    this.loadSensorData();
    this.loadChartData();
    this.initChart();

    // Update system metrics every 30 seconds
    setInterval(() => this.updateSystemMetrics(), 30000);
  }

  ngOnDestroy() {
    if (this.dataSubscription) {
      this.dataSubscription.unsubscribe();
    }
    if (this.chart) {
      this.chart.destroy();
    }
  }

  loadSensorData(): void {
    this.sensorService.getECSensors().subscribe(
      (data) => {
        this.sensors = data;
      },
      (error) => {
        console.error('Error fetching sensor data:', error);
        // Fallback to mock data
        this.loadMockSensorData();
      }
    );
  }

  loadMockSensorData(): void {
    this.sensors = [
      { id: 1, name: 'Temperature', value: '24Â°C', icon: 'ðŸŒ¡ï¸', type: 'Environmental', unit: 'Â°C', status: 'normal', isOnline: true, lastReading: new Date() },
      { id: 2, name: 'Humidity', value: '65%', icon: 'ðŸ’§', type: 'Environmental', unit: '%', status: 'normal', isOnline: true, lastReading: new Date() },
      { id: 3, name: 'Light', value: '850 lux', icon: 'â˜€ï¸', type: 'Environmental', unit: 'lux', status: 'normal', isOnline: true, lastReading: new Date() },
      { id: 4, name: 'EC', value: '1.8 mS/cm', icon: 'âš¡', type: 'Nutrient', unit: 'mS/cm', status: 'warning', isOnline: false, lastReading: new Date() },
      { id: 5, name: 'pH', value: '6.2', icon: 'ðŸ§ª', type: 'Nutrient', unit: '', status: 'normal', isOnline: true, lastReading: new Date() },
      { id: 6, name: 'COâ‚‚', value: '450 ppm', icon: 'ðŸŒ«ï¸', type: 'Gas', unit: 'ppm', status: 'normal', isOnline: true, lastReading: new Date() }
    ];
  }

  loadChartData(): void {
    this.sensorService.getSensorHistory('defaultSensorId').subscribe(
      (data) => {
        this.processChartData(data);
      },
      (error) => {
        console.error('Error fetching chart data:', error);
        // Fallback to mock chart data
        this.loadMockChartData();
      }
    );
  }

  processChartData(data: any): void {
    // Process and format real data for chart
    this.chartData = {
      labels: data.timestamps,
      datasets: data.datasets
    };
  }

  loadMockChartData(): void {
    const labels = Array.from({ length: 24 }, (_, i) => `${i}:00`);

    this.chartData = {
      labels,
      datasets: [
        {
          label: 'Temperature (Â°C)',
          data: [22, 22.5, 23, 23.5, 24, 24.5, 25, 25.5, 26, 26.5, 26, 25.5, 25, 24.5, 24, 23.5, 23, 22.5, 22, 21.5, 21, 21.5, 22, 22],
          borderColor: '#006d5b',
          tension: 0.4,
          fill: false
        },
        {
          label: 'Humidity (%)',
          data: [60, 61, 63, 64, 65, 67, 68, 70, 71, 72, 70, 68, 67, 65, 63, 62, 60, 59, 58, 57, 58, 59, 60, 60],
          borderColor: '#8d90bb',
          tension: 0.4,
          fill: false
        },
        {
          label: 'Light (lux/100)',
          data: [0, 0, 0, 1, 3, 5, 7, 8, 8.5, 8.5, 8.5, 8.5, 8.5, 8, 7, 5, 3, 1, 0, 0, 0, 0, 0, 0],
          borderColor: '#ffc107',
          tension: 0.4,
          fill: false
        }
      ]
    };
  }

  startIrrigation(): void {
    this.sensorService.startIrrigation().subscribe(
      () => alert('Irrigation started successfully!'),
      (error) => console.error('Error starting irrigation:', error)
    );
  }

  exportData(): void {
    this.sensorService.exportData().subscribe(
      (data) => {
        const blob = new Blob([data], { type: 'application/json' }); // Set MIME type to JSON
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.setAttribute('hidden', '');
        a.setAttribute('href', url);
        a.setAttribute('download', 'eden-link-data-export.json'); // Update file extension to .json
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
      },
      (error) => console.error('Error exporting data:', error)
    );
  }

  private initChart() {
    this.dataSubscription = this.sensorDataService.getSensorData().subscribe(data => {
      if (!this.chart && this.chartCanvas) {
        this.createChart(data);
      } else if (this.chart) {
        this.updateChart(data);
      }
    });
  }

  private createChart(data: SensorData[]) {
    const ctx = this.chartCanvas.nativeElement.getContext('2d');

    const config: ChartConfiguration = {
      type: 'line',
      data: {
        labels: data.map(d => this.formatTimestamp(d.timestamp)),
        datasets: [
          {
            label: 'Temperature (Â°C)',
            data: data.map(d => d.temperature),
            borderColor: 'rgb(255, 99, 132)',
            tension: 0.3,
            yAxisID: 'temperature'
          },
          {
            label: 'Humidity (%)',
            data: data.map(d => d.humidity),
            borderColor: 'rgb(54, 162, 235)',
            tension: 0.3,
            yAxisID: 'humidity'
          },
          {
            label: 'Light (lux)',
            data: data.map(d => d.light),
            borderColor: 'rgb(255, 205, 86)',
            tension: 0.3,
            yAxisID: 'light'
          }
        ]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        animation: {
          duration: 0
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Time'
            }
          },
          temperature: {
            type: 'linear',
            position: 'left',
            title: {
              display: true,
              text: 'Temperature (Â°C)'
            },
            min: 15,
            max: 30
          },
          humidity: {
            type: 'linear',
            position: 'right',
            title: {
              display: true,
              text: 'Humidity (%)'
            },
            min: 55,
            max: 85
          },
          light: {
            type: 'linear',
            position: 'right',
            title: {
              display: true,
              text: 'Light (lux)'
            },
            min: 1500,
            max: 6500,
            grid: {
              drawOnChartArea: false
            }
          }
        },
        plugins: {
          title: {
            display: true,
            text: 'Greenhouse Environmental Conditions',
            font: {
              size: 16
            }
          },
          legend: {
            position: 'bottom'
          }
        }
      }
    };

    this.chart = new Chart(ctx, config);
  }

  private updateChart(data: SensorData[]) {
    if (!this.chart) return;

    this.chart.data.labels = data.map(d => this.formatTimestamp(d.timestamp));
    this.chart.data.datasets[0].data = data.map(d => d.temperature);
    this.chart.data.datasets[1].data = data.map(d => d.humidity);
    this.chart.data.datasets[2].data = data.map(d => d.light);

    this.chart.update();
  }

  private formatTimestamp(timestamp: Date): string {
    return new Date(timestamp).toLocaleTimeString();
  }

  // Add method to determine system health status based on metrics
  private updateSystemHealthStatus(): void {
    const cpuCritical = this.systemHealth.cpuUsage > 90;
    const memoryCritical = this.systemHealth.memoryUsage > 90;
    const storageCritical = this.systemHealth.storageUsage > 90;

    const cpuWarning = this.systemHealth.cpuUsage > 80;
    const memoryWarning = this.systemHealth.memoryUsage > 80;
    const storageWarning = this.systemHealth.storageUsage > 80;

    if (cpuCritical || memoryCritical || storageCritical) {
      this.systemHealth.status = 'error';
      this.systemHealth.message = 'Critical: System resources are severely constrained. Immediate attention required.';
    } else if (cpuWarning || memoryWarning || storageWarning) {
      this.systemHealth.status = 'warning';
      this.systemHealth.message = 'Warning: System resources are running high. Consider optimization.';
    } else if (this.systemHealth.cpuUsage > 70 || this.systemHealth.memoryUsage > 70 || this.systemHealth.storageUsage > 70) {
      this.systemHealth.status = 'good';
      this.systemHealth.message = 'System is functioning well but resource usage is elevated.';
    } else {
      this.systemHealth.status = 'excellent';
      this.systemHealth.message = 'All systems functioning optimally.';
    }
  }

  // Call this method periodically or when metrics update
  private updateSystemMetrics(): void {
    // In a real application, these would come from a service
    this.systemHealth.cpuUsage = Math.round(45 + Math.random() * 10);
    this.systemHealth.memoryUsage = Math.round(60 + Math.random() * 10);
    this.systemHealth.storageUsage = Math.round(55 + Math.random() * 5);
    this.updateSystemHealthStatus();
  }
}
