import { Component, OnInit, OnDestroy } from '@angular/core';
import { SensorService } from '../../core/services/sensor.service';
import { AiService } from '../../core/services/ai.service';
import { CropService } from '../../core/services/crop.service';
import { interval, Subject, combineLatest } from 'rxjs';
import { takeUntil, map } from 'rxjs/operators';
import { Sensor } from '../../core/models/sensor.model';
import { ChartConfiguration } from 'chart.js';
import { Crop, CropStage } from '../../core/models/crop.model';

interface CurrentMetrics {
  temperature: number;
  humidity: number;
  light: number;
  ec: number;
  ph: number;
}

interface ChartData {
  labels: string[];
  datasets: Array<{
    label: string;
    data: number[];
    borderColor: string;
    backgroundColor: string;
    tension: number;
    fill: boolean;
    borderWidth: number;
  }>;
}

interface RoadmapStage {
  name: string;
  completed: boolean;
  active: boolean;
  description?: string;
  startDate?: Date;
  requiredActions?: string[];
}

interface CropCycle {
  crop: Crop;
  stages: RoadmapStage[];
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();
  private updateInterval$ = interval(5000);

  currentMetrics: CurrentMetrics = {
    temperature: 24,
    humidity: 65,
    light: 650,
    ec: 1.8,
    ph: 6.2
  };

  systemHealth = {
    status: 'excellent',
    message: 'All systems functioning optimally. Consider adjusting CO₂ levels slightly for optimal tomato growth.'
  };

  chartData: ChartData | null = null;

  chartOptions: ChartConfiguration['options'] = {
    responsive: true,
    maintainAspectRatio: false,
    animation: {
      duration: 750,
      easing: 'easeInOutQuart'
    },
    scales: {
      x: {
        grid: {
          color: 'rgba(0, 0, 0, 0.1)',
          borderColor: 'rgba(0, 0, 0, 0.3)'
        },
        ticks: {
          color: '#666',
          font: {
            size: 11
          }
        },
        title: {
          display: true,
          text: 'Time',
          color: '#666',
          font: {
            size: 12,
            weight: 'bold'
          }
        }
      },
      y: {
        grid: {
          color: 'rgba(0, 0, 0, 0.1)',
          borderColor: 'rgba(0, 0, 0, 0.3)'
        },
        ticks: {
          color: '#666',
          font: {
            size: 11
          }
        }
      }
    },
    plugins: {
      legend: {
        position: 'top',
        align: 'center',
        labels: {
          boxWidth: 15,
          padding: 15,
          font: {
            size: 11
          }
        }
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        titleColor: '#000',
        bodyColor: '#666',
        borderColor: 'rgba(0, 0, 0, 0.1)',
        borderWidth: 1,
        padding: 10,
        bodyFont: {
          size: 11
        },
        titleFont: {
          size: 12,
          weight: 'bold'
        }
      }
    },
    interaction: {
      mode: 'index',
      axis: 'x',
      intersect: false
    }
  };

  currentCrop: Crop | null = null;
  roadmapStages: RoadmapStage[] = [];
  cropCycles: CropCycle[] = [];

  constructor(
    private sensorService: SensorService,
    private aiService: AiService,
    private cropService: CropService
  ) {}

  ngOnInit(): void {
    this.loadInitialData();
    this.setupRealTimeUpdates();
    this.loadCurrentCrop();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  private loadInitialData(): void {
    this.loadSensorData();
  }

  private setupRealTimeUpdates(): void {
    this.updateInterval$.pipe(
      takeUntil(this.destroy$)
    ).subscribe(() => {
      this.loadSensorData();
    });
  }

  private loadSensorData(): void {
    // Get history for all sensor types
    const sensorIds = ['temp-1', 'humidity-1', 'light-1', 'ec-1', 'ph-1'];
    const histories$ = sensorIds.map(id => this.sensorService.getSensorHistory(id, 1));

    combineLatest(histories$).pipe(
      takeUntil(this.destroy$),
      map(histories => {
        // Update current metrics with the latest values
        this.updateCurrentMetrics(histories);
        // Process data for the chart
        return this.processHistoriesIntoChartData(histories);
      })
    ).subscribe({
      next: (chartData: ChartData) => {
        this.chartData = chartData;
      },
      error: (error: Error) => {
        console.error('Error loading sensor data:', error);
        this.loadMockData();
      }
    });
  }

  private updateCurrentMetrics(histories: any[][]): void {
    // Get the most recent values from each history
    const getLatestValue = (history: any[]) => {
      if (!history || history.length === 0) return null;
      const latestReading = history[history.length - 1];
      return Number(latestReading.value);
    };

    const temperature = getLatestValue(histories[0]);
    const humidity = getLatestValue(histories[1]);
    const light = getLatestValue(histories[2]);
    const ec = getLatestValue(histories[3]);
    const ph = getLatestValue(histories[4]);

    // Update current metrics with real values or fallback to defaults
    this.currentMetrics = {
      temperature: temperature ?? 24,
      humidity: humidity ?? 65,
      light: light ?? 650,
      ec: ec ?? 1.8,
      ph: ph ?? 6.2
    };

    // Update system health status based on current metrics
    this.updateSystemHealth();
  }

  private updateSystemHealth(): void {
    const isOptimal = (value: number, min: number, max: number) => 
      value >= min && value <= max;

    const allOptimal = 
      isOptimal(this.currentMetrics.temperature, 22, 26) &&
      isOptimal(this.currentMetrics.humidity, 60, 70) &&
      isOptimal(this.currentMetrics.light, 500, 800) &&
      isOptimal(this.currentMetrics.ec, 1.5, 2.1) &&
      isOptimal(this.currentMetrics.ph, 5.8, 6.6);

    const someWarning = 
      !isOptimal(this.currentMetrics.temperature, 20, 28) ||
      !isOptimal(this.currentMetrics.humidity, 55, 75) ||
      !isOptimal(this.currentMetrics.light, 400, 900) ||
      !isOptimal(this.currentMetrics.ec, 1.3, 2.3) ||
      !isOptimal(this.currentMetrics.ph, 5.5, 6.9);

    if (allOptimal) {
      this.systemHealth = {
        status: 'excellent',
        message: 'All systems functioning optimally. Plant growth conditions are ideal.'
      };
    } else if (someWarning) {
      this.systemHealth = {
        status: 'warning',
        message: 'Some parameters are outside optimal range. Consider adjusting environmental controls.'
      };
    } else {
      this.systemHealth = {
        status: 'critical',
        message: 'Critical: Multiple parameters require immediate attention.'
      };
    }
  }

  private processHistoriesIntoChartData(histories: any[][]): ChartData {
    const labels = histories[0]?.map(h => new Date(h.timestamp).toLocaleTimeString()) ?? [];
    const datasets = [
      {
        label: 'Temperature (°C)',
        data: histories[0]?.map(h => Number(h.value)) ?? [],
        borderColor: '#006d5b',
        backgroundColor: 'rgba(0, 109, 91, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Humidity (%)',
        data: histories[1]?.map(h => Number(h.value)) ?? [],
        borderColor: '#8d90bb',
        backgroundColor: 'rgba(141, 144, 187, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'Light (lux/100)',
        data: histories[2]?.map(h => Number(h.value)) ?? [],
        borderColor: '#ffc107',
        backgroundColor: 'rgba(255, 193, 7, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'EC (mS/cm)',
        data: histories[3]?.map(h => Number(h.value)) ?? [],
        borderColor: '#dc3545',
        backgroundColor: 'rgba(220, 53, 69, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      },
      {
        label: 'pH',
        data: histories[4]?.map(h => Number(h.value)) ?? [],
        borderColor: '#6610f2',
        backgroundColor: 'rgba(102, 16, 242, 0.1)',
        tension: 0.4,
        fill: true,
        borderWidth: 2
      }
    ];

    return { labels, datasets };
  }

  private loadMockData(): void {
    const now = new Date();
    const labels = Array.from({ length: 12 }, (_, i) => {
      const d = new Date(now.getTime() - (11 - i) * 5000);
      return d.toLocaleTimeString('en-US', { hour12: false });
    });

    // Generate random values that stay within realistic ranges
    const temperature = 22 + Math.random() * 4;
    const humidity = 60 + Math.random() * 12;
    const light = Math.max(0, 5 + Math.random() * 3) * 100;
    const ec = 1.5 + Math.random() * 0.6;
    const ph = 5.8 + Math.random() * 0.8;

    // Update current metrics with the latest mock values
    this.currentMetrics = {
      temperature,
      humidity,
      light,
      ec,
      ph
    };

    // Update system health based on new values
    this.updateSystemHealth();

    this.chartData = {
      labels,
      datasets: [
        {
          label: 'Temperature (°C)',
          data: Array.from({ length: 12 }, (_, i) => i === 11 ? temperature : 22 + Math.random() * 4),
          borderColor: '#006d5b',
          backgroundColor: 'rgba(0, 109, 91, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'Humidity (%)',
          data: Array.from({ length: 12 }, (_, i) => i === 11 ? humidity : 60 + Math.random() * 12),
          borderColor: '#8d90bb',
          backgroundColor: 'rgba(141, 144, 187, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'Light (lux/100)',
          data: Array.from({ length: 12 }, (_, i) => i === 11 ? light/100 : Math.max(0, 5 + Math.random() * 3)),
          borderColor: '#ffc107',
          backgroundColor: 'rgba(255, 193, 7, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'EC (mS/cm)',
          data: Array.from({ length: 12 }, (_, i) => i === 11 ? ec : 1.5 + Math.random() * 0.6),
          borderColor: '#dc3545',
          backgroundColor: 'rgba(220, 53, 69, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        },
        {
          label: 'pH',
          data: Array.from({ length: 12 }, (_, i) => i === 11 ? ph : 5.8 + Math.random() * 0.8),
          borderColor: '#6610f2',
          backgroundColor: 'rgba(102, 16, 242, 0.1)',
          tension: 0.4,
          fill: true,
          borderWidth: 2
        }
      ]
    };
  }

  startIrrigation(): void {
    this.sensorService.startIrrigation().subscribe(
      () => {
        console.log('Irrigation started');
      },
      error => {
        console.error('Error starting irrigation:', error);
      }
    );
  }

  exportData(): void {
    this.sensorService.exportData().subscribe(
      (data) => {
        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'sensor-data.json';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
      },
      error => {
        console.error('Error exporting data:', error);
      }
    );
  }

  private loadCurrentCrop(): void {
    this.cropService.getCrops({ status: 'active' }).pipe(
      takeUntil(this.destroy$)
    ).subscribe({
      next: (crops) => {
        if (crops.length > 0) {
          // Create cycles for each crop
          this.cropCycles = crops.map(crop => ({
            crop,
            stages: this.createRoadmapStages(crop)
          }));
        }
      },
      error: (error) => {
        console.error('Error loading current crops:', error);
        // Add mock data for demonstration
        this.addMockCrops();
      }
    });
  }

  private addMockCrops(): void {
    const mockCrops = [
      {
        name: 'Tomatoes',
        plantedDate: new Date(2024, 2, 1),
        expectedHarvestDate: new Date(2024, 5, 15),
        currentStage: {
          name: 'Flowering',
          description: 'Plants are developing flowers which will turn into fruits.',
          startDate: new Date(2024, 3, 15),
          requiredActions: [
            'Monitor temperature between 20-24°C',
            'Maintain humidity around 65-75%',
            'Ensure proper pollination'
          ]
        }
      },
      {
        name: 'Coriander',
        plantedDate: new Date(2024, 3, 1),
        expectedHarvestDate: new Date(2024, 4, 15),
        currentStage: {
          name: 'Vegetation',
          description: 'Plants are developing lush green leaves.',
          startDate: new Date(2024, 3, 15),
          requiredActions: [
            'Maintain soil moisture',
            'Ensure good air circulation',
            'Monitor for leaf spots'
          ]
        }
      }
    ];

    this.cropCycles = mockCrops.map(crop => ({
      crop,
      stages: this.createRoadmapStages(crop)
    }));
  }

  private createRoadmapStages(crop: Crop): RoadmapStage[] {
    const stages = crop.name.toLowerCase() === 'coriander' 
      ? ['Germination', 'Seedling', 'Vegetation', 'Mature', 'Harvest']  // Coriander-specific stages
      : ['Germination', 'Seedling', 'Vegetation', 'Flowering', 'Fruiting', 'Harvest']; // Default stages

    const currentStageIndex = stages.findIndex(stage => 
      stage.toLowerCase() === crop.currentStage.name.toLowerCase()
    );

    return stages.map((stageName, index) => {
      const isCurrentStage = index === currentStageIndex;
      const isCompleted = index < currentStageIndex;

      return {
        name: stageName,
        completed: isCompleted,
        active: isCurrentStage,
        description: isCurrentStage ? crop.currentStage.description : undefined,
        startDate: isCurrentStage ? crop.currentStage.startDate : undefined,
        requiredActions: isCurrentStage ? crop.currentStage.requiredActions : undefined
      };
    });
  }
}
